import React, { useState, useEffect } from 'react';
import { PlayArrow, Add, ThumbUpAltOutlined, ThumbDownAltOutlined } from '@material-ui/icons';
import axios from 'axios';

import './listItem.scss';


export default function ListItem({ index, item }) {
    const [isHovered, setIsHovered] = useState(false);
    const [movie, setMovie] = useState({});

    useEffect(() => {
        const getMovie = async () => {
            try {
                const res = await axios('/movies/find/' + item, {
                    headers :{
                        token: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxMWE0OWNjZDZiNWJiOTA0Zjg0ZmQ0MCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYyOTEzMjA5MiwiZXhwIjoxNjI5NTY0MDkyfQ.2iuQWwT2D0E23AONA-M5zgXjBzvCYD5M3IBQg_NocrA'
                    },
                });
                setMovie(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        getMovie()
    }, [item]);
    return (
        <div className='listItem' 
            style={{left: isHovered && index * 225 -50 + index * 2.5}}
            onMouseEnter={() => setIsHovered(true)} 
            onMouseLeave={() => setIsHovered(false)}
            >
            <img src={movie.img}
                alt=''
            />
            {isHovered && (
                <>
                    <video src={movie.trailer} autoPlay={true} loop />
                    <div className='itemInfo'>
                        <div className='icons'>
                            <PlayArrow className='icon' />
                            <Add className='icon' />
                            <ThumbUpAltOutlined className='icon' />
                            <ThumbDownAltOutlined className='icon' />
                        </div>
                        <div className='itemInfoTop'>
                            <span>{movie.duration}</span>
                            <span className='limit'>+{movie.limit}</span>
                            <span>{movie.year}</span>
                        </div>
                        <div className='desc'>
                            {movie.desc}
                        </div>
                        <div className='genre'>{movie.genre}</div>
                    </div>
                </>
            )}
        </div>
    )
}
